import React, { Component } from 'react'
import { connect } from 'react-redux'
import { saveAnswer } from '../actions/users'

class QuestionCard extends Component {
  state={
    option: '',
  }

  addOptionToState = (e) => {
    e.preventDefault()
    const { dispatch } = this.props
    this.setState({ option: e.target.value})
  }

  handleSubmitAnswer = (e) => {
    e.preventDefault()
    const { dispatch, logedUserId, id } = this.props
    const { option } = this.state
    dispatch(saveAnswer(logedUserId, id,'optionOne'))
  }

  render() {
    const { author, optionOne, optionTwo, addOptionToState}= this.props

    return(
      <div className='question-card'>
        <h2 className='special-text'>Would you rather...</h2>
          <form className='form-vote'>
             <input type='radio' name={optionOne} value={optionOne} onChange= {this.addOptionToState} />{optionOne}<br/>
             <input type='radio' name={optionTwo} value={optionTwo} onChange= {this.addOptionToState} />{optionTwo}<br/>
             <input type='submit' value='Vote' onClick={this.handleSubmitAnswer} />
          </form>
          <p></p>
        <hr />
        <div className='questionAuthor'>
          <p>{author}</p>
         <div
         className='user-avatar'
         style= {{
                backgroundColor: 'LightSteelBlue',
              }}
         ></div>
        </div>
      </div>
    )
  }
}
