import React, { Component } from 'react'
import { connect } from 'react-redux'
import Menu from './Menu'
import { handleSaveAnswer } from '../actions/shared'


class QuestionCard extends Component {
  state= {
    option: '',
    answered: this.props.location.state.answered,
  }

  addOptionToState = (e) => {
        this.setState({ option: e.target.value })
  }

  handleSubmitAnswer = (e) => {
    e.preventDefault()
    const { dispatch, logedUserId } = this.props
    // const { id } = this.props.location.state
    const id = this.props.match.params.id
    const { option } = this.state

    dispatch(handleSaveAnswer(logedUserId, id, option ))
    .then(() => this.setState({ answered: true }))
  }

  render() {
    const { question,logedUserAnswers, avatars}= this.props
   const { answered } = this.state
   const {author} = question
   console.log(author)

   const userAvatar = avatars.filter(
      (a) => (a[0]===author)
    )

    console.log(userAvatar)

       return(
         <div >
           <Menu />
            <div className='container card'>

             <div className='questionAuthor'>
               <p >author</p>
               <div className='user-avatar'
                style= {{
                       backgroundColor: 'LightSteelBlue' ,

                       backgroundSize: '80px 100px',
                     }}
               ></div>
             </div>
           </div>
       </div>
       )
  }
}

function mapStateToProps(state, props) {
  const {questions, logedUser, users} = state
  const qId = props.match.params.id.trim()
  console.log(qId)
  const logedUserId = logedUser.id

   const question = questions[qId]

  const logedUserAnswers = users[logedUserId].answers

  const usersId = Object.keys(users)

  const avatars = []
  usersId.map((user) =>
    avatars.push([user,users[user].avatarURL ])
  )

  return {
    logedUserId,
    questions,
    logedUserAnswers,
    avatars,
    question

  }
}

export default connect(mapStateToProps) (QuestionCard)

