import { _getUsers, _getQuestions, _saveQuestionAnswer, _saveQuestion  } from '../utils/_DATA'
import { receiveUsers, saveAnswerInUsers, addQuestionInU } from './users'
import { receiveQuestions, saveAnswerInQ, addQuestionInQ } from './questions'
import { setLogedUser} from './logedUser'

export function handleInitialDataUsers() {
  return (dispatch) =>  {
    return _getUsers()
      .then((users) => {
        dispatch(receiveUsers(users))
      })
  }
}
export function handleInitialDataQuestions() {
  return (dispatch) =>  {
    return _getQuestions()
      .then((questions) => {
        dispatch(receiveQuestions(questions))
      })
  }
}

export function handleSaveAnswer (authedUser, qid, answer) {
  return (dispatch) => {
    return _saveQuestionAnswer({
      authedUser,
      qid,
      answer})
      .then((users) => dispatch(saveAnswerInUsers(authedUser, qid, answer)))
      .then((guestions) => dispatch(saveAnswerInQ(authedUser, qid, answer)))
  }
}


export function handleAddQuestion (question) {
    console.log( question)
  return (dispatch) => {
    return _saveQuestion ({
      question
    })
    .then((question) => dispatch(addQuestionInQ(question)))
    .then((users) => dispatch(addQuestionInU(question.author,question.id)))
  }
}
