import React, { Component } from 'react'
import { connect } from 'react-redux'
import { Redirect } from 'react-router-dom'
import Menu from './Menu'
import User from './User'

class LeaderBoard extends Component {
  state= {
    authedUser: ''
  }

  render () {

    const {logedUserID, logedUserName,} = this.props
     if (logedUserID === '') {
       return (
             <Redirect to= '/login' />
       )
     }
    else {
      return (
        <div  className= 'container'>
          <Menu name = {logedUserName} />
          <p>Leader Board</p>
          <User />
        </div>
      )
    }
  }
}

function mapStateToProps({logedUser, users}) {
  const logedUserID = logedUser.id
  const logedUserName = logedUser.name
  const userIdArray = Object.keys(users)
  const leaderArray =[]

  userIdArray.map((user) => {
    const nrAnswers = Object.keys(users[user].answers).length
    const nrQestions = users[user].questions.length

    leaderArray.push([nrQestions+nrAnswers,user])
  })

  return {
    logedUserID,
    logedUserName,
    leaderArray
  }
}

export default connect(mapStateToProps)(LeaderBoard)
