import React, { Component } from 'react'
import { connect } from 'react-redux'
import { Redirect } from 'react-router-dom'
import Menu from './Menu'
import QuestionList from './QuestionList'

class Home extends Component {

  render () {
    const {logedUser} = this.props
     if (logedUser.logedUser === '') {
       return (
             <Redirect to= '/login' />
       )
     }

    else  {
      const {logedUserID, logedUserData, questionId } = this.props
      const logedUserAnswers = Object.keys(logedUserData.answers)
      // const checkID= (id) =>
      //   id !== logedUserAnswers[0]
      //
      // const unanswerd = questionId.filter((id) =>
      //   id !== logedUserAnswers[0])

      const unanswerd = questionId.forEach((uID) =>{
        const a = logedUserAnswers[0]
        if (uId === a) {
          questionId.splice(uId.index, 1)
          return questionId
        }}
      )

      return (
        <div className= 'container'>
          <Menu name = {logedUser}/>
          <div>
            <div> questionId
              <ul>
                {questionId.map((qId) => (
                  <li key={qId}>{qId}</li>
                ))
                }
              </ul>
            </div>
          </div>
          <div>
            <div> logedUserAnswers
              <ul>
                {logedUserAnswers.map((qId) => (
                  <li key={qId}>{qId}</li>
                ))
                }
              </ul>
            </div>
            <div> Unanswerd
              <ul>
                {unanswerd.map((qId) => (
                  <li key={qId}>{qId}</li>
                ))
                }
              </ul>
            </div>
          </div>
        </div>
      )
    }
  }
}
function mapStateToProps ({logedUser, users, questions}) {
  const logedUserID = Object.values(logedUser)
  const logedUserData = users[logedUserID]
  return {
    logedUser,
    logedUserID,
    logedUserData,
    questionId: Object.keys(questions).sort((a,b) => questions[b].timestamp - questions[a].timestamp),

  }
}
export default connect(mapStateToProps)(Home)
