import React, { Component } from 'react'
import { connect } from 'react-redux'
import { Redirect } from 'react-router-dom'
import Menu from './Menu'

class AddQuestion extends Component {
  state= {
    textOne:'',
    textTwo:'',
  }

  setTextToState = (event) => {
    this.setState({textOne: event.target.value})
  }

  render () {
    const {logedUserID, logedUserName, setTextToState} = this.props
    const { textOne, textTwo }= this.state
     if (logedUserID === '') {
       return (
             <Redirect to= '/login' />
       )
     }
    else {
      return (
        <div  className= 'container'>
          <Menu  name = {logedUserName} />
          <h1>Add Question </h1>
            <form >
              <label>optionOne</label><br/>
              <input type='text' onClick= {setTextToState} defaultValue='textOne'/><br/>
              <label>optionTwo</label><br/>
              <input type="text" value='' /><br/>
              <input type="submit" value='add question' />
            </form>
        </div>
      )
    }
  }
}
function mapStateToProps({logedUser}) {
  const logedUserID = logedUser.id
  const logedUserName = logedUser.name
  return {
    logedUserID,
    logedUserName,
  }
}

export default connect(mapStateToProps)(AddQuestion)
