import React, { Component } from 'react'
import { connect } from 'react-redux'
import { Redirect } from 'react-router-dom'
import Menu from './Menu'
import QuestionCard from './QuestionCard'

class Home extends Component {
  state= {
    authedUser: ''
  }
  //TODO: by loging out is to change authed user in store to null again
  render () {
    const {authedUser, questionId, question} = this.props

    if (authedUser=== null) {
      return (
            <Redirect to= '/login' />
      )
    }
    else {
      return (
        <div  className= 'container'>
          <Menu />
          <ul>
          { questionId.map((qId) => (
            <li key={qId}>{qId}
            <p><QuestionCard id={qId} /></p></li>

          ) )}
          </ul>
        </div>
      )
    }
  }
}

// todo add questions to props
function mapStateToProps({authedUser, questions}) {

  return {
    authedUser,
    questionId: Object.keys(questions).sort((a,b) => questions[b].timestamp - questions[a].timestamp)
  }
}

export default connect(mapStateToProps)(Home)
