import React, { Component } from 'react'
import { connect } from 'react-redux'
import { Redirect } from 'react-router-dom'
import Menu from './Menu'
import QuestionCard from './QuestionCard'

class QuestionList extends Component {
  state= {

  }
  //TODO: by loging out is to change authed user in store to null again
  render () {
    const {authedUserID, questionId, question} = this.props

    if (authedUserID=== null) {
      return (
            <Redirect to= '/login' />
      )
    }
    else {
      return (
        <div className='container'>
          <Menu />
            <div className='question-list'>
              <h1 className='special-text'>Questions List</h1>

              <ul>
                {questionId.map((qId) => (
                  <li key={qId}>
                    <QuestionCard id={qId} />
                  </li>
                ))}
              </ul>
            </div>
        </div>
      )
    }
  }
}

function mapStateToProps({questions, users} ) {

  return {
    // for checking if not to route to login
    //authedUserID: authedUser,
    // id of all the questions
    questionId: Object.keys(questions).sort((a,b) => questions[b].timestamp - questions[a].timestamp),
    // id of questions that were answeared by users
    answeredQuestions: users[authedUser].answers
  }
}

export default connect(mapStateToProps)(QuestionList)
