import React, { Component } from 'react'
import { connect } from 'react-redux'
import { Redirect } from 'react-router-dom'
import Menu from './Menu'
import QuestionList from './QuestionList'

class Home extends Component {
  state= {
    seeQuestion: true,
    linkText: 'Show Answered Questions'
  }

  toggleQuestions = () => {
    (this.state.seeQuestion) ?
      this.setState({seeQuestion: false, linkText: 'Show Unanswered Questions',})
    :
      this.setState({seeQuestion: true, linkText: 'Show Answered Questions',})
  }

  render () {
    const {logedUserID} = this.props
     if (logedUserID === '') {
       return (
             <Redirect to= '/login' />
       )
     }

    else  {
      const {logedUserID, logedUserName, logedUserData, questionId, } = this.props
      const { seeQuestion, linkText }= this.state
      const logedUserAnswers = Object.keys(logedUserData.answers)
      let unanswered =[];

       (() =>  {questionId.map((q) =>
          (!logedUserAnswers.includes(q)) &&
         unanswered.push(q)
       )})()


      return (
        <div className= 'container'>
          <Menu name = {logedUserName}/>
          <div>

            <button onClick={ this.toggleQuestions }>{linkText}</button>
//TODO change question list that it is using sometimes question card and sometimes answer card
          { (seeQuestion) ?
            <div>
              <QuestionList ids={unanswered} text= 'Unanswert Questions'/>
            </div>
            :
            <div>
              <QuestionList ids={logedUserAnswers} text= 'Answert Questions'/>
            </div>
           }

          </div>
        </div>
      )
    }
  }
}
function mapStateToProps ({logedUser, users, questions}) {
  const logedUserID = logedUser.id
  const logedUserName = logedUser.name
  const logedUserData = users[logedUserID]
  return {
    logedUserID,
    logedUserName,
    logedUserData,
    questionId: Object.keys(questions).sort((a,b) => questions[b].timestamp - questions[a].timestamp),
  }
}
export default connect(mapStateToProps)(Home)
