import React, { Component } from 'react'
import { connect } from 'react-redux'
import { setAuthedUser} from '../actions/authedUser'
import { Redirect } from 'react-router-dom'

class Login extends Component {

  state={
    selectedUser: 'johndoe',
    // TODO not to hardcode johndoe
    toHome: false
  }

  handleSetAuthedUser = (e) => {
    e.preventDefault()
    const { dispatch } = this.props
    dispatch(setAuthedUser(this.state.selectedUser))
  }

  setUserToState = (e) => {
    this.setState({selectedUser:e.target.value})
  }

  handleSubmit = () => {
    this.setState({ toHome: true })
  }

  render () {
    const { userId,   } = this.props
    const {  toHome } = this.state

    console.log(this.props)

    if (toHome) {
      return (
      <Redirect to= '/' />
      )
    }
    else {
      return (
        <div className='question-card'>
          <h2>Choose your user account and login.</h2>
          <form>
            <select name='users' onChange={this.setUserToState} >
              {userId.map((user) =>
                <option key={user} value={user}>{user}</option>
              )}
            </select>
            <br />
            <input type='submit' value='login' onClick= {this.handleSetAuthedUser} onSubmit= {this.handleSubmit}/>
          </form>
        </div>
      )
    }
  }
}

function mapStateToProps (users ) {
  const u = Object.entries(users)

  return {
    userId: Object.keys(u[0][1]).sort()
  }
}

export default connect(mapStateToProps)(Login)
