import { _getUsers } from '../utils/_DATA'
import { receiveUsers } from './users'
import { setAuthedUser} from '../actions/authedUser'



export function handleUsersInitialData() {
  return (dispatch) =>  {
    return _getUsers()
      .then((users) => {
        dispatch(receiveUsers(users))
        dispatch(setAuthedUser())
      })
  }
}
