import { RECEIVE_USERS } from '../actions/users'
import { SAVE_ANSWER } from '../actions/users'

export default function users (state={}, action) {
  switch(action.type) {
    case RECEIVE_USERS :
      return {
        ... state,
       ... action.users
      }
    case SAVE_ANSWER :
// logedUserId, qId, option
      return {
        ...state,
        [action.logedUserId]:{
          ...state[action.logedUserId],
          answers: {
            ...state[action.logedUserId].answers,
            [action.qId] : action.option
          }
        }
      }



    default :
      return state
  }
}
