import { RECEIVE_USERS } from '../actions/users'
import { SAVE_ANSWER } from '../actions/users'

export default function users (state={}, action) {
  switch(action.type) {
    case RECEIVE_USERS :
      return {
        ... state,
       ... action.users
      }
    case SAVE_ANSWER :
      const {users} = action
      return {
        ... state,
        users

      }
    default :
      return state
  }
}
